    public function nombreCoursAndroidJanvier() {
        $query = $this->getEntityManager()
                ->createQuery("select count(c.nomCours) from MoocMoocBundle:Coursuivi cs,MoocMoocBundle:Cours c where cs.idCours=c.idcours and c.nomCours='Android' and cs.dateDebut between :deb and :fin ");
        $query->setParameters(array(
            'deb' => new \DateTime('2016-01-01'),
            'fin' => new \DateTime('2016-01-31')
                )
        );
        return $query->getSingleScalarResult();
    }
    
