                ->createQuery("select count(c.nomCours) from MoocMoocBundle:Coursuivi cs,MoocMoocBundle:Cours c where cs.idCours=c.idcours and c.nomCours='J2ME' and cs.dateDebut between :deb and :fin ");
        $query->setParameters(array(
            'deb' => new \DateTime('2016-12-01'),
            'fin' => new \DateTime('2016-12-31')
                )
        );
        return $query->getSingleScalarResult();
    }
}
